#include<iostream>using namespace std;enum CPU_Rank{ i3 = 2, i5, i7 };class CPU{private:	CPU_Rank Rank;	int frequency;	float voltage;public:    void GetRank();	void GetFrequency();	void GetVoltage();	void SetRank(CPU_Rank r);	void SetFrequency(int f);	void SetVoltage(float v);	void Run();	void Stop();};void CPU::Run(){	cout << "CPU开始运行!" << endl;}void CPU::Stop(){	cout << "CPU停止运行!" << endl;}void CPU::SetRank(CPU_Rank r){	r = i3;}void CPU::SetFrequency(int f){	frequency = f;}void CPU::SetVoltage(float v){	voltage = v;}inline void CPU::GetRank(){	cout << i3 << endl;}inline void CPU::GetFrequency(){	cout << frequency << endl;}inline void CPU::GetVoltage(){	cout << voltage << endl;}int main(){	CPU myCPU;	myCPU.SetRank(i3);	myCPU.SetFrequency(50);	myCPU.SetVoltage(3.8);	myCPU.GetRank();	myCPU.GetFrequency();	myCPU.GetVoltage();	myCPU.Run();	myCPU.Stop();	return 0;}