#include<iostream>using namespace std;enum CPU_Rank{ i3 = 2, i5, i7 };class CPU{private:	CPU_Rank Rank1;	CPU_Rank Rank2;	int frequency;	float voltage;public:	CPU(CPU_Rank Rank1, int newf, float newv);	CPU()	{		Rank2 = i5;		frequency = 100;		voltage = 5.4;	}	void SetRank(CPU_Rank Rank1);	void SetFrequency(int f);	void SetVoltage(float v);	void GetRank1();	void GetRank2();	void GetFrequency();	void GetVoltage();	void Run();	void Stop();};CPU::CPU(CPU_Rank Rank1, int newf, float newv){	Rank1 = i3;	frequency = newf;	voltage = newv;	cout << "正在构造对象" << endl;}void CPU::Run(){	cout << "CPU开始运行!" << endl;}void CPU::Stop(){	cout << "CPU停止运行!" << endl;}void CPU::SetRank(CPU_Rank Rank1){	Rank2 = i5;}void CPU::SetFrequency(int f){	frequency = f;}void CPU::SetVoltage(float v){	voltage = v;}void CPU::GetRank1(){	cout << i3 << endl;}void CPU::GetRank2(){	cout << i5 << endl;}void CPU::GetFrequency(){	cout << frequency << endl;}void CPU::GetVoltage(){	cout << voltage << endl;}int main(){	CPU yourCPU(i3, 50, 3.8);	yourCPU.GetRank1();	yourCPU.GetFrequency();	yourCPU.GetVoltage();	CPU myCPU;	myCPU.SetRank(i5);	myCPU.SetFrequency(100);	myCPU.SetVoltage(5.4);	myCPU.GetRank2();	myCPU.GetFrequency();	myCPU.GetVoltage();	myCPU.Run();	myCPU.Stop();	return 0;}